pub fn solve_p1() -> () {
    let matrix: Vec<Vec<char>> = INPUT.lines().map(|line| line.chars().collect()).collect();
    let lines: Vec<String> = INPUT.lines().map(|l| l.to_string()).collect();
    let line_length = lines[0].len();
    let columns: Vec<String> = (0..line_length)
        .map(|col| matrix.iter().map(|line| line[col]).collect())
        .collect();

    let first_diagonal1: Vec<String> = (0..line_length)
        .map(|i| (0..line_length - i).map(|j| matrix[j][j + i]).collect())
        .collect();
    let first_diagonal2: Vec<String> = (1..line_length)
        .map(|i| (0..line_length - i).map(|j| matrix[i + j][j]).collect())
        .collect();

    let second_diagonal1: Vec<String> = (0..line_length)
        .map(|i| (0..=i).map(|j| matrix[i - j][j]).collect())
        .collect();
    let second_diagonal2: Vec<String> = (1..line_length)
        .map(|i| {
            (0..line_length - i)
                .map(|j| matrix[i + j][line_length - 1 - j])
                .collect()
        })
        .collect();

    let result = count_xmas(lines)
        + count_xmas(columns)
        + count_xmas(first_diagonal1)
        + count_xmas(first_diagonal2)
        + count_xmas(second_diagonal1)
        + count_xmas(second_diagonal2);

    println!("{result}");
}

fn count_xmas(strings: Vec<String>) -> usize {
    strings
        .iter()
        .map(|s| s.matches("XMAS").count() + s.matches("SAMX").count())
        .sum()
}

pub fn solve_p2() -> () {
    let matrix: Vec<Vec<char>> = INPUT.lines().map(|line| line.chars().collect()).collect();
    let amount_of_lines = matrix.len();
    let result: usize = (0..amount_of_lines - 2)
        .map(|top_left_i| {
            (0..amount_of_lines - 2)
                .filter(|top_left_j| is_x_mas(top_left_i, *top_left_j, &matrix))
                .count()
        })
        .sum();

    println!("{result}");
}

fn is_x_mas(top_left_i: usize, top_left_j: usize, matrix: &Vec<Vec<char>>) -> bool {
    let slice:Vec<Vec<char>> = matrix[top_left_i..=top_left_i + 2].to_vec().iter().map(|row| row[top_left_j..=top_left_j + 2].to_vec()).collect();
    let diagonal1 =
        first_diagonal(&slice);
    let diagonal2 =
        second_diagonal(&slice);
    (diagonal1 == "MAS" || diagonal1 == "SAM") && (diagonal2 == "MAS" || diagonal2 == "SAM")
}

fn first_diagonal(mat:&Vec<Vec<char>>) -> String {
    vec!(mat[0][0],mat[1][1],mat[2][2]).iter().collect()
}
fn second_diagonal(mat:&Vec<Vec<char>>) -> String {
    vec!(mat[0][2],mat[1][1],mat[2][0]).iter().collect()
}

